sys_variable_value.value:
(function execute(inputs, outputs) {
	outputs.status = inputs.status;
	if (inputs.status == 'success') {
		try {
			var resp = JSON.parse(inputs.sapresponse);
			var isNotEmpty = sn_sap_s4_hana_rfc.SapRFCUtil.isResponseNotEmpty(resp);
			if (isNotEmpty) {
				var errStatusObj = sn_sap_s4_hana_rfc.SapRFCUtil.getVendorErrors(resp);
				outputs.vendor = errStatusObj.response;
				outputs.error = errStatusObj.err;
				outputs.status = errStatusObj.status;
			} else {
				outputs.status = 'error';
				outputs.error = 'No or empty response received from SAP S4 HANA - RFC.';
			}
		} catch (e) {
			var errStatObj = sn_sap_s4_hana_rfc.SapRFCUtil.getUnknownErr(e);
			outputs.status = errStatObj.status;
			outputs.error = errStatObj.err;
		}
	}
})(inputs, outputs);

