sys_variable_value.value:
(function execute(inputs, outputs) {

    try {
        var inputs = SapRFCUtil.trimInputs(inputs);
        var mandatoryFields = {};
        mandatoryFields['Vendor Number'] = inputs['LFA1']['LIFNR'];
        mandatoryFields['Name'] = inputs['LFA1']['NAME1'];
       	mandatoryFields['Sort Field'] = inputs['LFA1']['SORTL'];
        mandatoryFields['Country Key'] = inputs['LFA1']['LAND1'];
        mandatoryFields['Vendor Account Group'] = inputs['LFA1']['KTOKK'];
        if (SapRFCUtil.checkEmptyProperties(inputs['LFB1']) != true) {
            mandatoryFields['Account Number of Vendor or Creditor'] = inputs['LFB1']['LIFNR'];
            mandatoryFields['Company Code'] = inputs['LFB1']['BUKRS'];
            mandatoryFields['Reconciliation Account in General Ledger'] = inputs['LFB1']['AKONT'];
        }
        if (SapRFCUtil.checkEmptyProperties(inputs['LFM1']) != true) {
            mandatoryFields['Account Number of Vendor or Creditor'] = inputs['LFM1']['LIFNR'];
            mandatoryFields['Purchasing Organization'] = inputs['LFM1']['EKORG'];
            mandatoryFields['Purchase Order Currency'] = inputs['LFM1']['WAERS'];
        }
        if (SapRFCUtil.checkEmptyProperties(inputs['LFBK']) != true) {
            mandatoryFields['Account Number of Vendor or Creditor'] = inputs['LFBK']['LIFNR'];
            mandatoryFields['Bank Account Number'] = inputs['LFBK']['BANKN'];
            mandatoryFields['Bank Country Key'] = inputs['LFBK']['BANKS'];
            mandatoryFields['Bank Key'] = inputs['LFBK']['BANKL'];
            mandatoryFields['Bank Name'] = inputs['LFBK']['BANKA'];
        }
        var emptyFields = SapRFCUtil.checkMandatoryFields(mandatoryFields);
        if (!gs.nil(emptyFields)) {
            throw 'Mandatory field(s) '.concat(emptyFields) + ' cannot be blank.';
        }

      	inputs['LFA1']['ERDAT'] = SapRFCUtil.getDateFormat(inputs['LFA1']['ERDAT']);
        inputs['LFB1']['ERDAT'] = SapRFCUtil.getDateFormat(inputs['LFB1']['ERDAT']);
        inputs['LFB1']['CERDT'] = SapRFCUtil.getDateFormat(inputs['LFB1']['CERDT']);
        inputs['LFB1']['QSZDT'] = SapRFCUtil.getDateFormat(inputs['LFB1']['QSZDT']);
        inputs['LFB1']['ZINDT'] = SapRFCUtil.getDateFormat(inputs['LFB1']['ZINDT']);
        inputs['LFM1']['MEPRF'] = SapRFCUtil.getDateFormat(inputs['LFM1']['MEPRF']);
        inputs['LFM1']['ERDAT'] = SapRFCUtil.getDateFormat(inputs['LFM1']['ERDAT']);
        inputs['LFBK']['KOVON'] = SapRFCUtil.getDateFormat(inputs['LFBK']['KOVON']);
        inputs['LFBK']['KOBIS'] = SapRFCUtil.getDateFormat(inputs['LFBK']['KOBIS']);

        var info = SapRFCUtil.getConnectionFromAlias(inputs.alias_id);
        outputs.conn_host = info.getAttribute("host");
        outputs.conn_params = info.getExtendedAttributes();
        var connId = info.getAttribute("sys_id");
        outputs.cred_id = SapRFCUtil.getCredentialIdFromConnection(connId);
        var inputPayload = {};
        inputPayload['IS_VENDOR'] = {
            'LFA1': inputs['LFA1'],
            'LFB1': inputs['LFB1'],
            'LFM1': inputs['LFM1'],
          	'LFBK': inputs['LFBK']
        };
        inputPayload['IV_SCENARIO'] = 'U';
        outputs.payload = JSON.stringify(inputPayload);
    } catch (e) {
        throw e;
    }

})(inputs, outputs);

