sys_variable_value.value:
(function execute(inputs, outputs) {
    outputs.status = inputs.status;
    if (inputs.status == 'success') {
        try {
            //Parse stringified JSON to JS Object
            outputs.response = JSON.parse(inputs.sapresponse);

            //Retrieve SAP execution errors if any and update status
            var errStatusObj = SapRFCUtil.getReturnErrors(outputs.response);
            outputs.status = errStatusObj.status;
            outputs.err = errStatusObj.err;
        } catch (e) {
            var errStatObj = SapRFCUtil.getUnknownErr(e);
            outputs.status = errStatObj.status;
            outputs.err = errStatObj.err;
        }
    }
})(inputs, outputs);

