sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
     	var inputs = SapRFCUtil.trimInputs(inputs);
      	inputs.materialDocItem = SapRFCUtil.filterArrayObjects(inputs.materialDocItem)

      //Validate mandatory fields
        var mandatoryFieldsList = {
            "Material Document Number": inputs.materialDocNumber,
            "Material Document Year": inputs.materialDocYear,
            "Posting Date": inputs.postingDate
        };

        var emptyFields = SapRFCUtil.checkMandatoryFields(mandatoryFieldsList);
        if (JSUtil.notNil(emptyFields)) {
            throw 'Mandatory field(s) '.concat(emptyFields) + ' cannot be blank.';
        }

        var info = SapRFCUtil.getConnectionFromAlias(inputs.alias_id);
        outputs.conn_host = info.getAttribute("host");
        outputs.conn_params = info.getExtendedAttributes();

        var connId = info.getAttribute("sys_id");
        outputs.cred_id = SapRFCUtil.getCredentialIdFromConnection(connId);
        var payload = SapRFCUtil.unflattenPayload(inputs.additional_in);

       if (inputs.bapi == "BAPI_GOODSMVT_CANCEL") {
            payload['MATERIALDOCUMENT'] = inputs.materialDocNumber;
            payload['MATDOCUMENTYEAR'] = inputs.materialDocYear;
            payload['GOODSMVT_PSTNG_DATE'] = inputs.postingDate;
            payload['GOODSMVT_PR_UNAME'] = inputs.userName;
            if (inputs.materialDocItem.length != 0) {
                payload['GOODSMVT_MATDOCITEM'] = inputs.materialDocItem;
            }
        } else if (inputs.bapi == "ZSN_BAPI_GOODSMVT_CANCEL") {
            payload['IV_MATERIALDOCUMENT'] = inputs.materialDocNumber;
            payload['IV_MATDOCUMENTYEAR'] = inputs.materialDocYear;
            payload['IV_GOODSMVT_PSTNG_DATE'] = inputs.postingDate;
            payload['IV_GOODSMVT_PR_UNAME'] = inputs.userName;
            if (inputs.materialDocItem.length != 0) {
                payload['IT_GOODSMVT_MATDOCITEM'] = inputs.materialDocItem;
            }
        } else {
            throw "The selected BAPI is not valid."
        }
        outputs.payload = SapRFCUtil.removeMetaNameField(payload);
    } catch (e) {
        throw e;
    }
})(inputs, outputs);

